import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-calculo-cdb',
  templateUrl: './calculo-cdb.component.html',
  styleUrls: ['./calculo-cdb.component.css']
})
export class CalculoCdbComponent {
 teste: string = "";
  constructor(http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    http.get<any>(baseUrl + 'cdb').subscribe(result => {
      this.teste = result;
      console.log(result);
    }, error => console.error(error));
  }
}
<h2>Cálculo do CDB</h2>
<div class="container">
    <form action="">
        <div class="form-group">
            <label for="valorMonetario">Valor Monetário</label>
            <input type="text" name="valorMonetario" id="valorMonetario" class="form-control" placeholder="Preencha o valor inicial do investimento">
        </div>
        <div class="form-group">
            <label for="prazo">Prazo</label>
            <input type="number" name="prazo" id="prazo" class="form-control" placeholder="Preencha o prazo em meses">
        </div>
        <div class="row">   
            <button type="submit" class="btn btn-primary btnCalcular">Calcular</button>           
        </div>

    </form>
</div>
