import { Component } from '@angular/core';
import { Cdb } from './cdb.model';
import { Form, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { CdbService } from './cdb.service';

@Component({
  selector: 'app-calculo-cdb',
  templateUrl: './calculo-cdb.component.html',
  styleUrls: ['./calculo-cdb.component.css']
})
export class CalculoCdbComponent {
 cdbForm: FormGroup;
 grossResult: number = 0;
 liquidResult: number = 0;

  constructor(private fb: FormBuilder, private service: CdbService) {
    this.cdbForm = this.fb.group({
      cash: [null, Validators.required],
      deadline: [null, this.validateDeadlineMoreThenOne]
    });
  }

  send() {
    if (!this.cdbForm.invalid) {
      const cdb: Cdb = new Cdb(
        this.cdbForm.value.cash,
        this.cdbForm.value.deadline
      );
      this.service.calculate(cdb).subscribe(
        (data) => {
          this.grossResult = data.grossResult
          this.liquidResult = data.liquidResult;
        },
        (error) => {
          console.log(cdb);
        }
      );    
      return;
    }
  }

  validateDeadlineMoreThenOne(input: FormControl){
    return ( input.value > 1 ? null : { invalid: true });
  }
}
<div class="container">
    <h2>Cálculo do CDB</h2>
    <form [formGroup]="cdbForm" novalidate (submit)="send()" >
        <div class="form-group">
            <label for="valorMonetario">Valor Monetário</label>
            <input type="text" formControlName="cash" id="valorMonetario" class="form-control" placeholder="Preencha o valor inicial do investimento">
            <span class="text-danger" *ngIf="cdbForm.controls.cash.value < 1 && cdbForm.controls.cash.touched" >
                O Valor monetário deve ser maior do que 0.
            </span>
            <span class="text-danger" *ngIf="!cdbForm.controls.cash.value && cdbForm.controls.cash.touched" >
                O Valor monetário deve ser preenchido.
            </span>
        </div>
        <div class="form-group">
            <label for="prazo">Prazo (em dias)</label>
            <input type="number" formControlName="deadline" id="prazo" class="form-control" placeholder="Preencha o prazo em meses">
            <span class="text-danger" *ngIf="cdbForm.controls.deadline.value < 2 && cdbForm.controls.deadline.touched " >
                O prazo para resgate da aplicação deve ser maior que 1.
            </span>
            <span class="text-danger" *ngIf="!cdbForm.controls.deadline.value && cdbForm.controls.deadline.touched " >
                O prazo para resgate da aplicação deve ser preenchido.
            </span>
        </div>
        <button type="submit" class="btn btn-primary w-100 mt-2" [disabled]="!cdbForm.valid">Calcular</button>           
        <div class="card mt-2">
            <div class="card-body">
              <h5 class="card-title">Resultado Bruto: {{grossResult}}</h5>
            </div>
        </div>
        <div class="card mt-2">
            <div class="card-body">
              <h5 class="card-title">Resultado Líquido: {{liquidResult}}</h5>
            </div>
        </div>
        {{cdbForm.value | json}}
    </form>
</div>
