import { Component } from '@angular/core';
import { Cdb } from './cdb.model';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CdbService } from './cdb.service';

@Component({
  selector: 'app-calculo-cdb',
  templateUrl: './calculo-cdb.component.html',
  styleUrls: ['./calculo-cdb.component.css']
})
export class CalculoCdbComponent {
 cdbForm: FormGroup;


  constructor(private fb: FormBuilder, private service: CdbService) {
    this.cdbForm = this.fb.group({
      valorMonetario: [null, Validators.required],
      prazo: ['', Validators.required]
    });
  }

  enviar(){

    if (this.cdbForm.invalid) {
      const cdb: Cdb = new Cdb(
        this.cdbForm.get('valorMonetario')?.value,
        this.cdbForm.get('prazo')?.value
      );
      this.service.calcular(cdb).subscribe(
        (data) => {
          console.log(cdb);
        },
        (error) => {
          console.log(cdb);
        }
      );    
      return;
    }
  }
}
<div class="container">
    <h2>Cálculo do CDB</h2>
    <form [formGroup]="cdbForm" (submit)="enviar()" >
        <div class="form-group">
            <label for="valorMonetario">Valor Monetário</label>
            <input type="text" name="valorMonetario" id="valorMonetario" class="form-control" placeholder="Preencha o valor inicial do investimento">
        </div>
        <div class="form-group">
            <label for="prazo">Prazo (em dias)</label>
            <input type="number" name="prazo" id="prazo" class="form-control" placeholder="Preencha o prazo em meses">
        </div>
        <button type="submit" class="btn btn-primary w-100 mt-2"  >Calcular</button>           
    </form>
</div>
