import { Component } from '@angular/core';
import { Cdb } from './cdb.model';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CdbService } from './cdb.service';

@Component({
  selector: 'app-calculo-cdb',
  templateUrl: './calculo-cdb.component.html',
  styleUrls: ['./calculo-cdb.component.css']
})
export class CalculoCdbComponent {
 cdbForm: FormGroup;


  constructor(private fb: FormBuilder, private service: CdbService) {
    this.cdbForm = this.fb.group({
      valorMonetario: [null, Validators.required],
      prazo: ['', Validators.required]
    });
  }

  enviar(){

    if (!this.cdbForm.invalid) {
      const cdb: Cdb = new Cdb(
        this.cdbForm.value.valorMonetario,
        this.cdbForm.value.prazo
      );
      this.service.calcular(cdb).subscribe(
        (data) => {
          console.log(data);
        },
        (error) => {
          console.log(cdb);
        }
      );    
      return;
    }
  }
}
<div class="container">
    <h2>Cálculo do CDB</h2>
    <form [formGroup]="cdbForm" novalidate (submit)="enviar()" >
        <div class="form-group">
            <label for="valorMonetario">Valor Monetário</label>
            <input type="text" formControlName="valorMonetario" id="valorMonetario" class="form-control" placeholder="Preencha o valor inicial do investimento">
        </div>
        <div class="form-group">
            <label for="prazo">Prazo (em dias)</label>
            <input type="number" formControlName="prazo" id="prazo" class="form-control" placeholder="Preencha o prazo em meses">
            <span class="text-danger" *ngIf="cdbForm.controls.prazo.value < 2" >
                O prazo para resgate da aplicação deve ser maior que 1.
            </span>
        </div>
        <button type="submit" class="btn btn-primary w-100 mt-2"  >Calcular</button>           
    </form>
</div>
